function burgerMenu(){const e=document.querySelector(".burger"),t=document.querySelector(".menu"),s=document.querySelectorAll(".menu")[1],i=document.querySelector("body");e.addEventListener("click",(()=>{t.classList.contains("active")||s.classList.contains("active")?(t.classList.remove("active"),s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked")):(t.classList.add("active"),s.classList.add("active"),e.classList.add("active-burger"),i.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(t.classList.remove("active"),s.classList.remove("active"),e.classList.remove("active-burger"),i.classList.remove("locked"))}))}burgerMenu(),$(".count").each((function(){$(this).prop("Counter",0).animate({Counter:$(this).text()},{duration:3e3,easing:"swing",step:function(e){$(this).text(Math.ceil(e))}})})),$(".count-three").each((function(){$(this).prop("Counter",100).animate({Counter:$(this).text()},{duration:3e3,easing:"swing",step:function(e){$(this).text(Math.ceil(e))}})}));